PostScript.Text:
if (animal.Movement == "Flight")
{
    animal.Movement = "Walk";
    animal.Strength += dice.D(1, 6);
}
else
{
    animal.Movement += " Flight";
    animal.Strength -= 1;
    animal.Armor -= 1;
}

if (animal.Movement == "Swimmer")
{
  animal.Features.Add("Speed x 2");
}
else
{
  animal.Movement += " Swimmer";
}
